package ab50;

public class Math_class {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		System.out.println(Math.addExact(10, 20));
		System.out.println(Math.subtractExact(50, 10));
		System.out.println(Math.multiplyExact(10, 10));
		System.out.println(Math.max(12.5, 22.20));
		System.out.println(Math.min(12.5, 22.20));
		System.out.println(Math.abs(-52));
		System.out.println(Math.random());   // range- 0.0 to 1
		System.out.println((int)(Math.random()*100));   // range- 1 to 100 type-cast
		double a =Math.random();
		double pi=Math.PI;
		System.out.println(pi);
	}

}
